import React, {ChangeEvent, MouseEvent} from 'react';
import c from './Dialogs.module.css';
import {DialogItem} from "./DialogItem/DialogsItem";
import {Message} from "./Message/Message";
import {AddMessageAC, dialogsPageType, StoreType, Types, UpdateNewMessageTextAC} from "../../Redax/state";

/*type DialogDataType = {
    id: number
    name: string
}
type MessageType = {
    id: number
    message: string
}*/
type PropsDialogType = {
    store: StoreType
}


const Dialogs: React.FC<PropsDialogType> = (props) => {
    let state = props.store.getState().dialogsPage

    const DialogsElement = state.DialogData.map(d => <DialogItem id={d.id} name={d.name}/>)
    const MessageElement = state.MessageData.map(m => <Message message={m.message}/>)
    const newMessageElement = React.createRef<HTMLTextAreaElement>()
    let newMassageBody = state.newMessage


    const addMessage = () => {

        if (newMessageElement.current) {
            
            props.store.dispatch(AddMessageAC())
            newMessageElement.current.value=''
        }
    }
    const onChangeNewMessage = (e: ChangeEvent<HTMLTextAreaElement>) => {
        const newMassage = e.currentTarget.value
        props.store.dispatch(UpdateNewMessageTextAC(newMassage))
    }
    return (
        <div className={c.dialogs}>
            <div className={c.dialogItem}>
                {DialogsElement}
            </div>
            <div className={c.messages}>
                {MessageElement}
            </div>
            <div>
                <textarea value={newMassageBody} onChange={onChangeNewMessage}
                          placeholder={'Enter the message'}></textarea>
            </div>
            <div>
                <button onClick={addMessage}>send message</button>
            </div>

        </div>
    )
}


export default Dialogs;
